public MutablePeriod(long duration, PeriodType type) {
super(duration, type, null);
}

protected final Object getParam() {
return iParam;
}

public void setInto(ReadWritablePeriod duration, Object object, Chronology chrono) {
duration.setPeriod((ReadablePeriod) object);
}

public Period(Object period) {
super(period, null, null);
}

public static DurationFieldType seconds() {
return SECONDS_TYPE;
}

protected PeriodType checkPeriodType(PeriodType type) {
return DateTimeUtils.getPeriodType(type);
}

public static ISOChronology getInstance() {
return getInstance(DateTimeZone.getDefault());
}

public int indexOf(DurationFieldType type) {
return getPeriodType().indexOf(type);
}
