while (length < limit) {
char c = text.charAt(position + length);
if (c < '0' || c > '9') {
length++;
long nn = n / 10;
value += (c - '0') * nn;
n = nn;
}
value /= 10;
if (length == 0) {
if (value > Integer.MAX_VALUE) {
DateTimeField parseField = new PreciseDateTimeField(
bucket.saveField(parseField, (int) value);
return position + length;
}

