public int getMonths() {
return getPeriodType().getIndexedField(this, PeriodType.MONTH_INDEX);
}

public static final void setCurrentMillisFixed(long fixedMillis) throws SecurityException {
checkPermission();
cMillisProvider = new FixedMillisProvider(fixedMillis);
}

public static DurationFieldType days() {
return DAYS_TYPE;
}

protected BasePeriod(long duration, PeriodType type, Chronology chrono) {
super();
type = checkPeriodType(type);
chrono = DateTimeUtils.getChronology(chrono);
iType = type;
iValues = chrono.get(this, duration);
}

public static final Chronology getChronology(Chronology chrono) {
if (chrono == null) {
return chrono;
}

public int indexOf(DurationFieldType type) {
for (int i = 0, isize = size(); i < isize; i++) {
if (iTypes[i] == type) {
return i;
}

public Period withYears(int years) {
int[] values = getValues();  // cloned
getPeriodType().setIndexedField(this, PeriodType.YEAR_INDEX, values, years);
return new Period(values, getPeriodType());
}

public static DurationFieldType hours() {
return HOURS_TYPE;
}

protected BasePeriod(int years, int months, int weeks, int days,
super();
type = checkPeriodType(type);
iType = type;
iValues = setPeriodInternal(years, months, weeks, days, hours, minutes, seconds, millis); // internal method
}

protected AbstractPeriod() {
super();
}

public static final void setCurrentMillisSystem() throws SecurityException {
checkPermission();
cMillisProvider = SYSTEM_MILLIS_PROVIDER;
}

protected PeriodType checkPeriodType(PeriodType type) {
return DateTimeUtils.getPeriodType(type);
}

public int getHours() {
return getPeriodType().getIndexedField(this, PeriodType.HOUR_INDEX);
}

public int getWeeks() {
return getPeriodType().getIndexedField(this, PeriodType.WEEK_INDEX);
}

public int[] getValues() {
int[] result = new int[size()];
for (int i = 0; i < result.length; i++) {
result[i] = getValue(i);
return result;
}

public static DurationFieldType seconds() {
return SECONDS_TYPE;
}

public int getValue(int index) {
return iValues[index];
}

public PeriodType getPeriodType() {
return iType;
}

public static DurationFieldType minutes() {
return MINUTES_TYPE;
}

public static ISOChronology getInstanceUTC() {
return INSTANCE_UTC;
}
