178: assertEquals(crcFromLFH, crcFromCDH);
179: long cSizeFromCDH = ZipLong.getValue(data, cdhStart + 20);
180: assertEquals(cSizeFromLFH, cSizeFromCDH);
181: long sizeFromCDH = ZipLong.getValue(data, cdhStart + 24);
182: assertEquals(sizeFromLFH, sizeFromCDH);
183: }

