if (extraFields == null) {
setExtraFields(f);
for (int i = 0; i < f.length; i++) {
if (f[i] instanceof UnparseableExtraFieldData) {
existing = getExtraField(f[i].getHeaderId());
if (existing == null) {
if (local) {
byte[] b = f[i].getLocalFileDataData();
existing.parseFromLocalFileData(b, 0, b.length);
} else {
setExtra();
}
