private QueryParser(String query) {
this.query = query;
this.tq = new TokenQueue(query);
}

public static Evaluator parse(String query) {
QueryParser p = new QueryParser(query);
return p.parse();
}

private void findElements() {
if (tq.matchChomp("#"))
else if (tq.matchChomp("."))
byClass();
else if (tq.matchesWord())
byTag();
else if (tq.matches("["))
byAttribute();
else if (tq.matchChomp("*"))
else if (tq.matchChomp(":lt("))
else if (tq.matchChomp(":gt("))
else if (tq.matchChomp(":eq("))
else if (tq.matches(":has("))
else if (tq.matches(":contains("))
else if (tq.matches(":containsOwn("))
else if (tq.matches(":matches("))
matches(false);
}

private void byTag() {
String tagName = tq.consumeElementSelector();
Validate.notEmpty(tagName);
if (tagName.contains("|"))
evals.add(new Evaluator.Tag(tagName.trim().toLowerCase()));
}

private void byAttribute() {
TokenQueue cq = new TokenQueue(tq.chompBalanced('[', ']')); // content queue
String key = cq.consumeToAny("=", "!=", "^=", "$=", "*=", "~="); // eq, not, start, end, contain, match, (no val)
Validate.notEmpty(key);
cq.consumeWhitespace();
if (cq.isEmpty()) {
if (cq.matchChomp("="))
evals.add(new Evaluator.AttributeWithValue(key, cq.remainder()));
}

private void matches(boolean own) {
tq.consume(own ? ":matchesOwn" : ":matches");
String regex = tq.chompBalanced('(', ')'); // don't unescape, as regex bits will be escaped
Validate.notEmpty(regex, ":matches(regex) query must not be empty");
if (own)
evals.add(new Evaluator.Matches(Pattern.compile(regex)));
}

public TokenQueue(String data) {
Validate.notNull(data);
queue = data;
}

public boolean isEmpty() {
return remainingLength() == 0;
}

private int remainingLength() {
return queue.length() - pos;
}

public boolean matches(String seq) {
return queue.regionMatches(true, pos, seq, 0, seq.length());
}

public boolean matchesAny(String... seq) {
for (String s : seq) {
if (matches(s))
return true;
return false;
}

public boolean matchesAny(char... seq) {
if (isEmpty())
for (char c: seq) {
if (queue.charAt(pos) == c)
return false;
}

public boolean matchChomp(String seq) {
if (matches(seq)) {
pos += seq.length();
return true;
return false;
}

public boolean matchesWhitespace() {
return !isEmpty() && StringUtil.isWhitespace(queue.charAt(pos));
}

public boolean matchesWord() {
return !isEmpty() && Character.isLetterOrDigit(queue.charAt(pos));
}

public char consume() {
return queue.charAt(pos++);
}

public void consume(String seq) {
if (!matches(seq))
int len = seq.length();
if (len > remainingLength())
pos += len;
}

public String consumeTo(String seq) {
int offset = queue.indexOf(seq, pos);
if (offset != -1) {
String consumed = queue.substring(pos, offset);
pos += consumed.length();
return consumed;
return remainder();
}

public String consumeToAny(String... seq) {
int start = pos;
while (!isEmpty() && !matchesAny(seq)) {
pos++;
String data = queue.substring(start, pos);
return data;
}

public String chompTo(String seq) {
String data = consumeTo(seq);
matchChomp(seq);
return data;
}

public String chompBalanced(char open, char close) {
StringBuilder accum = new StringBuilder();
int depth = 0;
char last = 0;
if (isEmpty()) break;
Character c = consume();
if (last == 0 || last != ESC) {
if (c.equals(open))
depth++;
else if (c.equals(close))
depth--;
if (depth > 0 && last != 0)
accum.append(c); // don't include the outer match pair in the return
last = c;
} while (depth > 0);
return accum.toString();
}

public boolean consumeWhitespace() {
boolean seen = false;
while (matchesWhitespace()) {
pos++;
seen = true;
return seen;
}

public String consumeElementSelector() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny('|', '_', '-')))
pos++;
return queue.substring(start, pos);
}

public String remainder() {
StringBuilder accum = new StringBuilder();
while (!isEmpty()) {
accum.append(consume());
return accum.toString();
}

public static Elements collect (Evaluator eval, Element root) {
Elements elements = new Elements();
new NodeTraversor(new Accumulator(root, elements, eval)).traverse(root);
return elements;
}

public Elements() {
contents = new ArrayList<Element>();
}

public NodeTraversor(NodeVisitor visitor) {
this.visitor = visitor;
}
