public static Evaluator parse(String query) {
QueryParser p = new QueryParser(query);
return p.parse();
}

Evaluator parse() {
tq.consumeWhitespace();
if (tq.matchesAny(combinators)) { // if starts with a combinator, use root as elements
findElements();
while (!tq.isEmpty()) {
boolean seenWhite = tq.consumeWhitespace();
if (tq.matchesAny(combinators)) {
combinator(tq.consume());
} else if (seenWhite) {
findElements(); // take next el, #. etc off queue
}
if (evals.size() == 1)
return evals.get(0);
return new CombiningEvaluator.And(evals);
}

private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
Evaluator newEval = parse(subQuery); // the evaluator to add into target evaluator
boolean replaceRightMost = false;
if (evals.size() == 1) {
rootEval = currentEval = new CombiningEvaluator.And(evals);
evals.clear();
if (combinator == '>')
else if (combinator == ' ')
else if (combinator == '+')
else if (combinator == '~')
else if (combinator == ',') { // group or.
if (currentEval instanceof CombiningEvaluator.Or) {
or = new CombiningEvaluator.Or();
or.add(currentEval);
or.add(newEval);
currentEval = or;
}
if (replaceRightMost)
else rootEval = currentEval;
evals.add(rootEval);
}

private String consumeSubQuery() {
StringBuilder sq = new StringBuilder();
while (!tq.isEmpty()) {
if (tq.matches("("))
else if (tq.matches("["))
sq.append("[").append(tq.chompBalanced('[', ']')).append("]");
else if (tq.matchesAny(combinators))
sq.append(tq.consume());
return sq.toString();
}

private void findElements() {
if (tq.matchChomp("#"))
else if (tq.matchChomp("."))
else if (tq.matchesWord())
byTag();
else if (tq.matches("["))
byAttribute();
}

private void byTag() {
String tagName = tq.consumeElementSelector();
Validate.notEmpty(tagName);
if (tagName.contains("|"))
evals.add(new Evaluator.Tag(tagName.trim().toLowerCase()));
}

private void byAttribute() {
TokenQueue cq = new TokenQueue(tq.chompBalanced('[', ']')); // content queue
String key = cq.consumeToAny(AttributeEvals); // eq, not, start, end, contain, match, (no val)
Validate.notEmpty(key);
cq.consumeWhitespace();
if (cq.isEmpty()) {
if (key.startsWith("^"))
evals.add(new Evaluator.Attribute(key));
if (cq.matchChomp("="))
evals.add(new Evaluator.AttributeWithValue(key, cq.remainder()));
}

public TokenQueue(String data) {
Validate.notNull(data);
queue = data;
}

public boolean isEmpty() {
return remainingLength() == 0;
}

private int remainingLength() {
return queue.length() - pos;
}

public boolean matches(String seq) {
return queue.regionMatches(true, pos, seq, 0, seq.length());
}

public boolean matchesAny(String... seq) {
for (String s : seq) {
if (matches(s))
return true;
return false;
}

public boolean matchesAny(char... seq) {
if (isEmpty())
for (char c: seq) {
if (queue.charAt(pos) == c)
return false;
}

public boolean matchChomp(String seq) {
if (matches(seq)) {
pos += seq.length();
return true;
return false;
}

public boolean matchesWhitespace() {
return !isEmpty() && StringUtil.isWhitespace(queue.charAt(pos));
}

public boolean matchesWord() {
return !isEmpty() && Character.isLetterOrDigit(queue.charAt(pos));
}

public char consume() {
return queue.charAt(pos++);
}

public String consumeToAny(String... seq) {
int start = pos;
while (!isEmpty() && !matchesAny(seq)) {
pos++;
return queue.substring(start, pos);
}

public String chompBalanced(char open, char close) {
int start = -1;
int end = -1;
int depth = 0;
char last = 0;
if (isEmpty()) break;
Character c = consume();
if (last == 0 || last != ESC) {
if (c.equals(open)) {
depth++;
if (start == -1)
start = pos;
else if (c.equals(close))
depth--;
if (depth > 0 && last != 0)
end = pos; // don't include the outer match pair in the return
last = c;
} while (depth > 0);
return (end >= 0) ? queue.substring(start, end) : "";
}

public boolean consumeWhitespace() {
boolean seen = false;
while (matchesWhitespace()) {
pos++;
seen = true;
return seen;
}

public String consumeElementSelector() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny('|', '_', '-')))
pos++;
return queue.substring(start, pos);
}

public String remainder() {
final String remainder = queue.substring(pos, queue.length());
pos = queue.length();
return remainder;
}

public static Elements collect (Evaluator eval, Element root) {
Elements elements = new Elements();
new NodeTraversor(new Accumulator(root, elements, eval)).traverse(root);
return elements;
}

public NodeTraversor(NodeVisitor visitor) {
this.visitor = visitor;
}

public void traverse(Node root) {
Node node = root;
int depth = 0;
while (node != null) {
visitor.head(node, depth);
if (node.childNodeSize() > 0) {
node = node.childNode(0);
depth++;
while (node.nextSibling() == null && depth > 0) {
visitor.tail(node, depth);
node = node.parentNode();
depth--;
visitor.tail(node, depth);
if (node == root)
break;
node = node.nextSibling();
}

Accumulator(Element root, Elements elements, Evaluator eval) {
this.root = root;
this.elements = elements;
this.eval = eval;
}

public void head(Node node, int depth) {
if (node instanceof Element) {
Element el = (Element) node;
if (eval.matches(root, el))
elements.add(el);
}
