void outerHtmlHead(StringBuilder accum, int depth, Document.OutputSettings out) {
String html = Entities.escape(getWholeText(), out);
if (out.prettyPrint() && parent() instanceof Element && !((Element) parent()).preserveWhitespace()) {
html = normaliseWhitespace(html);
if (out.prettyPrint() && siblingIndex() == 0 && parentNode instanceof Element && ((Element) parentNode).tag().canContainBlock() && !isBlank())
accum.append(html);
}

static String normaliseWhitespace(String text) {
text = StringUtil.normaliseWhitespace(text);
return text;
}

public void traverse(Node root) {
Node node = root;
int depth = 0;
while (node != null) {
visitor.head(node, depth);
if (node.childNodes().size() > 0) {
node = node.childNode(0);
depth++;
while (node.nextSibling() == null && depth > 0) {
visitor.tail(node, depth);
node = node.parent();
depth--;
visitor.tail(node, depth);
if (node == root)
break;
node = node.nextSibling();
}

public static String padding(int width) {
if (width < 0)
if (width < padding.length)
return padding[width];
}

public static String normaliseWhitespace(String string) {
StringBuilder sb = new StringBuilder(string.length());
boolean lastWasWhite = false;
boolean modified = false;
int l = string.length();
for (int i = 0; i < l; i++) {
int c = string.codePointAt(i);
if (Character.isWhitespace(c)) {
sb.appendCodePoint(c);
lastWasWhite = false;
return modified ? sb.toString() : string;
}

OuterHtmlVisitor(StringBuilder accum, Document.OutputSettings out) {
this.accum = accum;
this.out = out;
}

public void head(Node node, int depth) {
node.outerHtmlHead(accum, depth, out);
}

public void tail(Node node, int depth) {
if (!node.nodeName().equals("#text")) // saves a void hit.
node.outerHtmlTail(accum, depth, out);
}

