private void bufferUp() {
if (bufPos < bufSplitPoint)
return;
readerPos += bufPos;
reader.skip(bufPos);
reader.mark(maxBufferLen);
bufLength = reader.read(charBuf);
reader.reset();
bufPos = 0;
bufMark = 0;
bufSplitPoint = bufLength > readAheadLimit ? readAheadLimit : bufLength;
}
}

public ParseSettings(boolean tag, boolean attribute) {
preserveTagCase = tag;
preserveAttributeCase = attribute;
}

char consume() {
bufferUp();
char val = isEmpty() ? EOF : charBuf[bufPos];
bufPos++;
return val;
}

