public int read(byte[] b, int off, int len) throws IOException {
if (bytesRemaining == 0) {
int bytesToRead = len;
if (bytesToRead > bytesRemaining) {
final int bytesRead = file.read(b, off, bytesToRead);
if (bytesRead >= 0) {
bytesRemaining -= bytesRead;
