protected JsonProcessingException(String msg, JsonLocation loc, Throwable rootCause) {
super(msg);
if (rootCause != null) {
_location = loc;
}

protected JsonProcessingException(String msg, JsonLocation loc) {
this(msg, loc, null);
}

@Override public String getMessage() {
