public static Attribute createFromEncoded(String unencodedKey, String encodedValue) {
String value = Entities.unescape(encodedValue);
return new Attribute(unencodedKey, value);
}

public TextNode(String text, String baseUri) {
this.baseUri = baseUri;
this.text = text;
}

public static TextNode createFromEncoded(String encodedText, String baseUri) {
String text = Entities.unescape(encodedText);
return new TextNode(text, baseUri);
}

public static Document load(File in, String charsetName, String baseUri) throws IOException {
InputStream inStream = null;
inStream = new FileInputStream(in);
return load(inStream, charsetName, baseUri);
if (inStream != null)
inStream.close();
}

public static Document load(InputStream in, String charsetName, String baseUri) throws IOException {
ByteBuffer byteData = readToByteBuffer(in);
return parseByteData(byteData, charsetName, baseUri);
}

static Document parseByteData(ByteBuffer byteData, String charsetName, String baseUri) {
Document doc = null;
if (charsetName == null) { // determine from meta. safe parse as UTF-8
Validate.notEmpty(charsetName, "Must set charset arg to character set of file to parse. Set to null to attempt to detect from HTML");
docData = Charset.forName(charsetName).decode(byteData).toString();
if (doc == null) {
doc = Jsoup.parse(docData, baseUri);
}

static ByteBuffer readToByteBuffer(InputStream inStream) throws IOException {
byte[] buffer = new byte[bufferSize];
ByteArrayOutputStream outStream = new ByteArrayOutputStream(bufferSize);
read  = inStream.read(buffer);
if (read == -1) break;
outStream.write(buffer, 0, read);
ByteBuffer byteData = ByteBuffer.wrap(outStream.toByteArray());
return byteData;
}

public XmlDeclaration(String data, String baseUri, boolean isProcessingInstruction) {
super(baseUri);
attributes.put(DECL_KEY, data);
this.isProcessingInstruction = isProcessingInstruction;
}

public DataNode(String data, String baseUri) {
super(baseUri);
attributes.put(DATA_KEY, data);
}

public Comment(String data, String baseUri) {
super(baseUri);
attributes.put(COMMENT_KEY, data);
