mt[k] = mt[k + (M - N)] ^ (y >>> 1) ^ MAG01[y & 0x1];
y = (mtNext & 0x80000000) | (mt[0] & 0x7fffffff);
mt[N - 1] = mt[M - 1] ^ (y >>> 1) ^ MAG01[y & 0x1];
mti = 0;
y = mt[mti++];
y ^=  y >>> 11;
y ^= (y <<   7) & 0x9d2c5680;
y ^= (y <<  15) & 0xefc60000;
y ^=  y >>> 18;
return y >>> (32 - bits);
}

