int lc = Integer.MIN_VALUE, sc = Integer.MAX_VALUE;
int lu = Integer.MIN_VALUE, su = Integer.MAX_VALUE;
for (Map.Entry<String, String> ent : uncompressSuffix.entrySet()) {
int cl = ent.getKey().length();
if (cl > lc) {
lc = cl;
if (cl < sc) {
sc = cl;
