if (!compressSuffix.containsKey(u)) {
compressSuffix.put(u, ent.getKey());
if (ul > lu) {
lu = ul;
if (ul < su) {
su = ul;
}
longestCompressedSuffix = lc;
longestUncompressedSuffix = lu;
shortestCompressedSuffix = sc;
shortestUncompressedSuffix = su;
