152: zae = zf.getEntry("test1.txt");
153: zos.addRawArchiveEntry(zae, zf.getRawInputStream(zae));
154: }
155: 156: byte[] data = o.toByteArray();
157: byte[] versionInLFH = Arrays.copyOfRange(data, 4, 6);
158: // still 2.0 because of Deflate
159: assertArrayEquals(new byte[] { 20, 0 }, versionInLFH);
160: byte[] gpbInLFH = Arrays.copyOfRange(data, 6, 8);
161: // no DD but EFS flag
162: assertArrayEquals(new byte[] { 0, 8 }, gpbInLFH);
163: 164: int cdhStart = findCentralDirectory(data);
165: byte[] versionInCDH = Arrays.copyOfRange(data, cdhStart + 6, cdhStart + 8);
166: assertArrayEquals(new byte[] { 20, 0 }, versionInCDH);
167: byte[] gpbInCDH = Arrays.copyOfRange(data, cdhStart + 8, cdhStart + 10);
168: assertArrayEquals(new byte[] { 0, 8 }, gpbInCDH);
169: 170: int ddStart = cdhStart - 16;
171: assertNotEquals(ZipLong.DD_SIG, new ZipLong(data, ddStart));
172: long crcFromLFH = ZipLong.getValue(data, 14);
173: long cSizeFromLFH = ZipLong.getValue(data, 18);
174: long sizeFromLFH = ZipLong.getValue(data, 22);
175: assertEquals(3, sizeFromLFH);
176: 177: long crcFromCDH = ZipLong.getValue(data, cdhStart + 16);
178: assertEquals(crcFromLFH, crcFromCDH);
179: long cSizeFromCDH = ZipLong.getValue(data, cdhStart + 20);
180: assertEquals(cSizeFromLFH, cSizeFromCDH);
181: long sizeFromCDH = ZipLong.getValue(data, cdhStart + 24);
182: assertEquals(sizeFromLFH, sizeFromCDH);
183: }

