public static long skip(InputStream input, long numToSkip) throws IOException {
long available = numToSkip;
while (numToSkip > 0) {
long skipped = input.skip(numToSkip);
if (skipped == 0) {
break;
numToSkip -= skipped;
}
while (numToSkip > 0) {
int read = readFully(input, SKIP_BUF, 0,
if (read < 1) {
break;
return available - numToSkip;
}

