bb.put(HEX_DIGITS[(c >> 4)&0x0f]);
bb.put(HEX_DIGITS[c & 0x0f]);
}

static ByteBuffer growBuffer(ByteBuffer b, int newCapacity) {
b.limit(b.position());
b.rewind();
int c2 = b.capacity() * 2;
ByteBuffer on = ByteBuffer.allocate(c2 < newCapacity ? newCapacity : c2);
on.put(b);
return on;
}

