private BitSet readBits(final DataInput header, final int size) throws IOException {
final BitSet bits = new BitSet(size);
int mask = 0;
int cache = 0;
for (int i = 0; i < size; i++) {
if (mask == 0) {
mask = 0x80;
cache = header.readUnsignedByte();
bits.set(i, (cache & mask) != 0);
mask >>>= 1;
return bits;
}

