38: @Test public void discardsSpuriousByteOrderMarkWhenNoCharsetSet() {
39: String html = "\uFEFF<html><head><title>One</title></head><body>Two</body></html>";
40: ByteBuffer buffer = Charset.forName("UTF-8").encode(html);
41: Document doc = DataUtil.parseByteData(buffer, null, "http://foo.com/", Parser.htmlParser());
42: assertEquals("One", doc.head().text());
43: assertEquals("UTF-8", doc.outputSettings().charset().displayName());
44: }
