167: @Test public void testIsValidDocument() {
168: String ok = "<html><head></head><body><p>Hello</p></body><html>";
169: String nok = "<html><head><script>woops</script><title>Hello</title></head><body><p>Hello</p></body><html>";
170: 171: Whitelist relaxed = Whitelist.relaxed();
172: Cleaner cleaner = new Cleaner(relaxed);
173: Document okDoc = Jsoup.parse(ok);
174: assertTrue(cleaner.isValid(okDoc));
175: assertFalse(cleaner.isValid(Jsoup.parse(nok)));
176: assertFalse(new Cleaner(Whitelist.none()).isValid(okDoc));
177: }

