76: assertEquals("http://www.w3.org/1999/xhtml", htmlEl.getNamespaceURI());
77: assertEquals("html", htmlEl.getLocalName());
78: assertEquals("html", htmlEl.getNodeName());
79: 80: // inherits default namespace
81: Node head = htmlEl.getFirstChild();
82: assertEquals("http://www.w3.org/1999/xhtml", head.getNamespaceURI());
83: assertEquals("head", head.getLocalName());
84: assertEquals("head", head.getNodeName());
