char current() {
return isEmpty() ? EOF : input.charAt(pos);
}

char consume() {
char val = isEmpty() ? EOF : input.charAt(pos);
pos++;
return val;
}

void unconsume() {
pos--;
}

void advance() {
pos++;
}

