private String queue;
private int pos = 0;
private static final Character ESC = '\\'; // escape char for chomp balanced.
public TokenQueue(String data) {
Validate.notNull(data);
queue = data;
}

public boolean isEmpty() {
return remainingLength() == 0;
}

private int remainingLength() {
return queue.length() - pos;
}

public Character peek() {
return isEmpty() ? null : queue.charAt(pos);
}

public boolean matches(String seq) {
int count = seq.length();
if (count > remainingLength())
while (--count >= 0) {
if (Character.toLowerCase(seq.charAt(count)) != Character.toLowerCase(queue.charAt(pos+count)))
return false;
return true;
}

public boolean matchesCS(String seq) {
return queue.startsWith(seq, pos);
}

public boolean matchesAny(String... seq) {
for (String s : seq) {
if (matches(s))
return true;
return false;
}

public boolean matchesAny(char... seq) {
if (isEmpty())
for (char c: seq) {
if (queue.charAt(pos) == c)
return true;
return false;
}

public boolean matchesStartTag() {
return (remainingLength() >= 2 && queue.charAt(pos) == '<' && Character.isLetterOrDigit(queue.charAt(pos+1)));
}

public boolean matchChomp(String seq) {
if (matches(seq)) {
pos += seq.length();
return true;
return false;
}

public boolean matchesWhitespace() {
return !isEmpty() && Character.isWhitespace(queue.charAt(pos));
}

public boolean matchesWord() {
return !isEmpty() && Character.isLetterOrDigit(queue.charAt(pos));
}

public Character consume() {
Character c = queue.charAt(pos);
pos++;
return c;
}

public void consume(String seq) {
if (!matches(seq))
int len = seq.length();
if (len > remainingLength())
pos += len;
}

public String consumeTo(String seq) {
int offset = queue.indexOf(seq, pos);
if (offset != -1) {
String consumed = queue.substring(pos, offset);
pos += consumed.length();
return consumed;
}

public String consumeToIgnoreCase(String seq) {
int start = pos;
String first = seq.substring(0, 1);
boolean canScan = first.toLowerCase().equals(first.toUpperCase()); // if first is not cased, use index of
while (!isEmpty() && !matches(seq)) {
if (canScan) {
int skip = queue.indexOf(first, pos) - pos;
if (skip <= 0)
pos++;
else if (skip < 0) // no chance of finding, grab to end
pos += skip;
}
String data = queue.substring(start, pos);
return data;
}

public String chompTo(String seq) {
String data = consumeTo(seq);
matchChomp(seq);
return data;
}

public String chompToIgnoreCase(String seq) {
String data = consumeToIgnoreCase(seq); // case insensitive scan
matchChomp(seq);
return data;
}

public boolean consumeWhitespace() {
boolean seen = false;
while (matchesWhitespace()) {
pos++;
seen = true;
return seen;
}

public String consumeTagName() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny(':', '_', '-')))
pos++;
return queue.substring(start, pos);
}

public String consumeAttributeKey() {
int start = pos;
while (!isEmpty() && (matchesWord() || matchesAny('-', '_', ':')))
pos++;
return queue.substring(start, pos);
}

protected Node(String baseUri, Attributes attributes) {
Validate.notNull(baseUri);
Validate.notNull(attributes);
childNodes = new ArrayList<Node>(4);
this.baseUri = baseUri.trim();
this.attributes = attributes;
}

protected Node(String baseUri) {
this(baseUri, new Attributes());
}

protected Node() {
childNodes = Collections.EMPTY_LIST;
attributes = null;
}

protected void setParentNode(Node parentNode) {
if (this.parentNode != null)
this.parentNode = parentNode;
}

protected void addChildren(Node... children) {
for (Node child: children) {
reparentChild(child);
childNodes.add(child);
child.setSiblingIndex(childNodes.size()-1);
}

private void reparentChild(Node child) {
if (child.parentNode != null)
child.setParentNode(this);
}

protected void setSiblingIndex(int siblingIndex) {
this.siblingIndex = siblingIndex;
}

public Element(Tag tag, String baseUri, Attributes attributes) {
super(baseUri, attributes);
Validate.notNull(tag);
this.tag = tag;
}

public Element(Tag tag, String baseUri) {
this(tag, baseUri, new Attributes());
}

public String tagName() {
return tag.getName();
}

public Tag tag() {
return tag;
}

public Element appendChild(Node child) {
Validate.notNull(child);
addChildren(child);
return this;
}

public Document(String baseUri) {
super(Tag.valueOf("#root"), baseUri);
}

public void put(String key, String value) {
Attribute attr = new Attribute(key, value);
put(attr);
}

public void put(Attribute attribute) {
Validate.notNull(attribute);
attributes.put(attribute.getKey(), attribute);
}

private Entities.EscapeMode escapeMode = Entities.EscapeMode.base;
private Charset charset = Charset.forName("UTF-8");
