private void combinator(char combinator) {
tq.consumeWhitespace();
String subQuery = consumeSubQuery(); // support multi > childs
Evaluator newEval = parse(subQuery); // the evaluator to add into target evaluator
boolean replaceRightMost = false;
if (evals.size() == 1) {
rootEval = currentEval = new CombiningEvaluator.And(evals);
evals.clear();
if (combinator == '>')
else if (combinator == ' ')
else if (combinator == '+')
else if (combinator == '~')
else if (combinator == ',') { // group or.
if (currentEval instanceof CombiningEvaluator.Or) {
or = new CombiningEvaluator.Or();
or.add(currentEval);
or.add(newEval);
currentEval = or;
}
if (replaceRightMost)
else rootEval = currentEval;
evals.add(rootEval);
}

static ByteBuffer readToByteBuffer(InputStream inStream, int maxSize) throws IOException {
Validate.isTrue(maxSize >= 0, "maxSize must be 0 (unlimited) or larger");
final boolean capped = maxSize > 0;
byte[] buffer = new byte[bufferSize];
ByteArrayOutputStream outStream = new ByteArrayOutputStream(bufferSize);
int remaining = maxSize;
read = inStream.read(buffer);
if (read == -1) break;
if (capped) {
outStream.write(buffer, 0, read);
ByteBuffer byteData = ByteBuffer.wrap(outStream.toByteArray());
return byteData;
}

