public Class<Factory> createProxyClass(Class<?> mockedType, Class<?>... interfaces) {
if (mockedType == Object.class) {
Enhancer enhancer = new Enhancer() {
Class<?>[] allMockedTypes = prepend(mockedType, interfaces);
